import React, { useState, useEffect } from 'react';

const Dashboard = () => {
  const [modoNoturno, setModoNoturno] = useState(false); // Estado do modo noturno

  // Função para alternar entre o modo noturno e o modo claro
  const alternarModoNoturno = () => {
    setModoNoturno(!modoNoturno);
  };

  // Adicione efeitos de modo noturno ao corpo do documento com base no estado
  useEffect(() => {
    if (modoNoturno) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [modoNoturno]);

  const [mostrarPopup, setMostrarPopup] = useState(false); // Estado do pop-up

  const alternarPopup = () => {
    setMostrarPopup(!mostrarPopup);
  };

  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [valorAGuardar, setValorAGuardar] = useState('');

  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);

  const handleAdicionar = () => {
    const valorGuardado = parseFloat(valorAGuardar) || 0;
    const saldoRestante = saldoTotal - totalDespesas;
  
    // Garantir que o valor a ser guardado não ultrapasse o saldo restante
    const valorAGuardarFinal = Math.min(valorGuardado, saldoRestante);
  
    const novoRegistro = { descricao, valor, data, categoria, valorAGuardar: valorAGuardarFinal };
  
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setValorAGuardar('');
    setContadorDespesas(contadorDespesas + 1);
  };

  const calcularSaldoDisponivel = () => {
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoDisponivel = saldoTotal - totalDespesas;
    return { saldoDisponivel, totalDespesas };
  };

  const calcularPorcentagemLucro = () => {
    const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();
    const porcentagemLucro = (saldoDisponivel / (totalDespesas || 1)) * 100;
    return porcentagemLucro;
  };

  // Restante do código para os gráficos e lógica relacionada...

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <button onClick={alternarPopup}>Abrir Popup</button>

      {mostrarPopup && (
        <div className="popup">
          <div className="popup-content">
            <button onClick={alternarPopup}>Fechar</button>
            {/* Conteúdo do pop-up */}
            <div className="add-expense-container">
              <div className="add-expense">
                <div className="saldo-total-input">
                  <label htmlFor="saldoTotal">Saldo Total:</label>
                  <input
                    type="number"
                    id="saldoTotal"
                    value={saldoTotal}
                    onChange={(e) => setSaldoTotal(e.target.value)}
                  />
                </div>
                <div className="description-input">
                  <label htmlFor="descricao">Gastos:</label>
                  <input
                    type="text"
                    id="descricao"
                    value={descricao}
                    onChange={(e) => setDescricao(e.target.value)}
                  />
                </div>
                {/* Restante dos campos do formulário */}
                <button onClick={handleAdicionar}>Adicionar Despesa</button>
              </div>
              <button className="modo-noturno-button" onClick={alternarModoNoturno}>
                {modoNoturno ? "Modo Claro" : "Modo Noturno"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
