import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [modoNoturno, setModoNoturno] = useState(false); // Estado do modo noturno
  // ... (rest of your existing code)

  // State for the new form fields
  const [quantidadeGuardar, setQuantidadeGuardar] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [iraRetirar, setIraRetirar] = useState('');

  // Function to handle form submission
  const handleFormSubmit = (e) => {
    e.preventDefault();
    // You can access the form values in these state variables (quantidadeGuardar, dataInicio, dataFim, iraRetirar)
    console.log("Quantidade a guardar:", quantidadeGuardar);
    console.log("Data de início:", dataInicio);
    consoleg.log("Data de fim:", dataFim);
    console.log("Irá retirar?", iraRetirar);
    // You can perform any further actions or validations here
  };

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="saldo-total-input">
            <label htmlFor="saldoTotal">Saldo Total:</label>
            <input
              type="number"
              id="saldoTotal"
              value={saldoTotal}
              onChange={(e) => setSaldoTotal(e.target.value)}
            />
          </div>
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          {/* ... (your existing input fields) */}
          <button onClick={handleAdicionar}>$</button>
          <button className="modo-noturno-button" onClick={alternarModoNoturno}>
            {modoNoturno ? "Modo Claro" : "Modo Noturno"}
          </button>
        </div>
        <div className="charts-container">
          {/* ... (your existing chart components) */}
        </div>
      </div>
      
      <div className="chart">
        <h4>Gráfico de Onda dos Maiores Gastos por Mês</h4>
        {waveChartData && (
          <ReactApexChart options={waveChartData.options} series={waveChartData.series} type="line" />
        )}
      </div>
      
      <div className="savings-form">
        <h4>Quanto disposto a guardar?</h4>
        <form onSubmit={handleFormSubmit}>
          <div className="savings-input">
            <label htmlFor="quantidadeGuardar">Quantidade a guardar:</label>
            <input
              type="number"
              id="quantidadeGuardar"
              value={quantidadeGuardar}
              onChange={(e) => setQuantidadeGuardar(e.target.value)}
            />
          </div>
          <div className="savings-input">
            <label htmlFor="dataInicio">Data de início:</label>
            <input
              type="date"
              id="dataInicio"
              value={dataInicio}
              onChange={(e) => setDataInicio(e.target.value)}
            />
          </div>
          <div className="savings-input">
            <label htmlFor="dataFim">Data de fim:</label>
            <input
              type="date"
              id="dataFim"
              value={dataFim}
              onChange={(e) => setDataFim(e.target.value)}
            />
          </div>
          <div className="savings-input">
            <label htmlFor="iraRetirar">Irá retirar?</label>
            <select
              id="iraRetirar"
              value={iraRetirar}
              onChange={(e) => setIraRetirar(e.target.value)}
            >
              <option value="">Selecione uma opção</option>
              <option value="Sim">Sim</option>
              <option value="Não">Não</option>
            </select>
          </div>
          <button type="submit">Enviar</button>
        </form>
      </div>
    </div>
  );
}

export default Dashboard;
