import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [modoNoturno, setModoNoturno] = useState(false); // Estado do modo noturno

  // Função para alternar entre o modo noturno e o modo claro
  const alternarModoNoturno = () => {
    setModoNoturno(!modoNoturno);
  };

  // Adicione efeitos de modo noturno ao corpo do documento com base no estado
  useEffect(() => {
    if (modoNoturno) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [modoNoturno]);

  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [valorAGuardar, setValorAGuardar] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);
  const [saldoIndisponivel, setSaldoIndisponivel] = useState(0);

  const [exibirChartPizza, setExibirChartPizza] = useState(false);
  
  const handleAdicionar = () => {
    const valorGuardado = parseFloat(valorAGuardar) || 0;
    const saldoRestante = saldoTotal - totalDespesas;
  
    // Garantir que o valor a ser guardado não ultrapasse o saldo restante
    const valorAGuardarFinal = Math.min(valorGuardado, saldoRestante);
  
    const novoRegistro = { descricao, valor, data, categoria, valorAGuardar: valorAGuardarFinal };
  
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setValorAGuardar('');
    setContadorDespesas(contadorDespesas + 1);
    setExibirChartPizza(true); // Mostrar o gráfico de pizza ao adicionar um novo registro
  };

  
  const calcularSaldoDisponivel = () => {
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoDisponivel = saldoTotal - totalDespesas;
    return { saldoDisponivel, totalDespesas };
  };

  const calcularPorcentagemLucro = () => {
    const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();
    const porcentagemLucro = (saldoDisponivel / (totalDespesas || 1)) * 100;
    return porcentagemLucro;
  };

  useEffect(() => {
    Chart.register(CategoryScale, LinearScale, BarElement, BarController);

    const chartData = {
      labels: ['Saldo Total', 'Saldo Disponível'],
      datasets: [
        {
          label: 'Saldo',
          data: [saldoTotal, calcularSaldoDisponivel().saldoDisponivel],
          backgroundColor: ['blue', 'red'],
        },
      ],
    };

    setChartData(chartData);
  }, [saldoTotal, registros]);

  const [chartData, setChartData] = useState(null);

  const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();

  const calcularDistribuicaoCategorias = () => {
    const categorias = {};
    registros.forEach((registro) => {
      const { categoria } = registro;
      categorias[categoria] = categorias[categoria] ? categorias[categoria] + 1 : 1;
    });

    const labels = Object.keys(categorias);
    const data = Object.values(categorias);

    setCategoriaData({
      options: {
        labels,
      },
      series: data,
      type: 'donut',
    });
  };

  useEffect(() => {
    calcularDistribuicaoCategorias();
  }, [registros]);

  const [categoriaData, setCategoriaData] = useState(null);

  const [waveChartData, setWaveChartData] = useState({
    options: {
      chart: {
        id: 'wave-chart',
        type: 'line',
        height: 350,
      },
      xaxis: {
        categories: ['Jan', 'Fev', 'Mar', 'Abr', 'Mai', 'Jun', 'Jul', 'Ago', 'Set', 'Out', 'Nov', 'Dez'],
      },
    },
    series: [
      {
        name: 'Gastos',
        data: [1000, 1500, 1200, 1800, 2000, 1600, 1700, 1900, 2100, 2200, 2400, 2600],
      },
    ],
  });

  const [lucroChartData, setLucroChartData] = useState({
    options: {
      chart: {
        id: 'lucro-chart',
        type: 'radialBar',
      },
      plotOptions: {
        radialBar: {
          hollow: {
            size: '70%',
          },
          dataLabels: {
            showOn: 'always',
          },
        },
      },
      labels: ['Lucro'],
    },
    series: [calcularPorcentagemLucro()],
  });

  useEffect(() => {
    const saldoRestante = saldoTotal - totalDespesas;
    const porcentagemAGuardar = (valorAGuardar / saldoRestante) * 100 || 0;

    const data = [porcentagemAGuardar, 100 - porcentagemAGuardar];

    const porcentagemGuardarDinheiroData = {
      options: {
        labels: ['A Guardar', 'Restante'],
      },
      series: data,
    };

    setPorcentagemGuardarDinheiro(porcentagemGuardarDinheiroData);
  }, [saldoTotal, totalDespesas, valorAGuardar]);

  const [porcentagemGuardarDinheiro, setPorcentagemGuardarDinheiro] = useState(null);

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="saldo-total-input">
            <label htmlFor="saldoTotal">Saldo Total:</label>
            <input
              type="number"
              id="saldoTotal"
              value={saldoTotal}
              onChange={(e) => setSaldoTotal(e.target.value)}
            />
          </div>
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <select
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione uma categoria</option>
              <option value="Conta de Luz">Conta de Luz</option>
              <option value="Conta de Telefone">Conta de Telefone</option>
              <option value="Conta de Água">Conta de Água</option>
              <option value="Contas de Casa">Contas de Casa</option>
              <option value="Gastos Emergenciais">Gastos Emergenciais</option>
              <option value="Compras">Compras</option>
            </select>
          </div>
          <div className="button-add">
        <button onClick={handleAdicionar}>Adicionar</button>
      </div> 
      
        </div>
        <div className="valor-container">
          <div className="value-to-save-input">
            <label htmlFor="valorAGuardar">Quanto a guardar de dinheiro?</label>
            <input
              type="number"
              id="valorAGuardar"
              value={valorAGuardar}
              onChange={(e) => setValorAGuardar(e.target.value)}
            />
          </div>
        
          <button className = "modo-button" onClick={handleAdicionar}>$</button>
          <button className="modo-noturno-button" onClick={alternarModoNoturno}>
            {modoNoturno ? "Modo Claro" : "Modo Noturno"}
          </button>
        </div>
        <div className= "borda"></div>
        <div className= "info-container">
        <div className="info">
                <div className="saldo-disponivel">
                  <p>Saldo Disponível:</p>
                  <span>{saldoDisponivel}</span>
                </div>
                <div className="total-despesas">
                  <p>Total de Despesas:</p>
                  <span>{totalDespesas}</span>
                
              </div>
      </div>   
      </div>     
        <div className="charts-container">
        <div className="chart-and-info">
        {chartData && (
                <Bar
                  data={chartData}
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                      },
                    },
                  }}
                />
              )}
        </div>
          <div className="chart-container">
            <h4></h4>
           
            
           
          </div>
       
        </div>
      </div>
      
      <div className="chart">
        <h4>Gráfico de Onda dos Maiores Gastos por Mês</h4>
        {waveChartData && (
          <ReactApexChart options={waveChartData.options} series={waveChartData.series} type="line" />
        )}
      </div>     
      <div className="chart">
        <h4>Porcentagem a Guardar de Dinheiro</h4>
        {porcentagemGuardarDinheiro && (
          <ReactApexChart options={porcentagemGuardarDinheiro.options} series={porcentagemGuardarDinheiro.series} type="donut" />
        )}
      </div>

      <div className="add-expense-container">
        
        <div className="body-container">
          {/* Condicional para renderizar o chart-pizza somente se exibirChartPizza for true */}
          {exibirChartPizza && (
            <div className="chart-pizza">
               <h4 className={modoNoturno ? 'text-night text-bold' : ''}>Maiores gastos</h4>
              <ReactApexChart
          
          options={{
            chart: {
              type: 'donut',
            },
            labels: ['Luz', 'Telefone', 'Água', 'Casa'],
            style: {
              color: modoNoturno ? '#fff' : '#000'
            },
          }}
          series={[20, 30, 50]}
          type="donut"
          width={380}
        />
            </div>
          )}
        </div>
      </div>
    
      
      <div className="table2">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
              <th>Quanto a guardar</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
                <td>{registro.valorAGuardar}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      
      <div className="charts">
        <div className="chart">
          {/* Conteúdo do gráfico */}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
