import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [modoNoturno, setModoNoturno] = useState(false); // Estado do modo noturno

  // Restante do seu código...

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="add-expense-container">
        <div className="add-expense">
          {/* Restante do seu código... */}
        </div>
        <div className="charts-container">
          <div className="chart-container">
            <h4>Saldo e Despesas</h4>
            {chartData && (
              <Bar
                data={chartData}
                options={{
                  scales: {
                    y: {
                      beginAtZero: true,
                    },
                  },
                }}
              />
            )}
            <div className="info">
              <div className="saldo-disponivel">
                <p>Saldo Disponível:</p>
                <span>{saldoDisponivel}</span>
              </div>
              <div className="total-despesas">
                <p>Total de Despesas:</p>
                <span>{totalDespesas}</span>
              </div>
            </div>
          </div>
          <div className="chart-pizza">
            <h4>Distribuição de Categorias</h4>
            {categoriaData && (
              <ReactApexChart options={categoriaData.options} series={categoriaData.series} type={categoriaData.type} />
            )}
          </div>
        </div>
      </div>

      <div className="chart">
        <h4>Gráfico de Onda dos Maiores Gastos por Mês</h4>
        {waveChartData && (
          <ReactApexChart options={waveChartData.options} series={waveChartData.series} type="line" />
        )}
      </div>

      <div className="chart">
        <h4>Porcentagem a Guardar de Dinheiro</h4>
        {porcentagemGuardarDinheiro && (
          <ReactApexChart options={porcentagemGuardarDinheiro.options} series={porcentagemGuardarDinheiro.series} type="donut" />
        )}
      </div>

      <div className="table2">
        {/* Restante do seu código... */}
      </div>

      <div className="charts">
        <div className="chart">
          {/* Conteúdo do gráfico */}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
