import React, { useState } from 'react';
import { FaChartBar, FaStar, FaMoneyBill, FaCalendarAlt } from 'react-icons/fa'; 

const Popup = ({ onClose }) => {
  return (
    <div className="popup">
      <div className="popup-content">
        {/* Conteúdo do popup */}
        <p>Conteúdo do popup aqui</p>
        <button onClick={onClose}>Fechar</button>
      </div>
    </div>
  );
};

const Menu = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const handleDashboardClick = () => {
    // Abre o popup ao clicar no ícone Dashboard
    setIsPopupOpen(true);
  };

  const closePopup = () => {
    // Fecha o popup
    setIsPopupOpen(false);
  };

  return (
    <div className="menu-top">
      {/* Verifica se o popup está aberto e renderiza */}
      {isPopupOpen && <Popup onClose={closePopup} />}
      <div className="icon-buttons">
        {/* Adiciona um evento de clique para o ícone Dashboard */}
        <a href="/dashboard" className="icon-dashboard" onClick={handleDashboardClick}>
          <FaChartBar className="icon-dashboard" />
        </a>
        <a href="/star" className="icon-star">
          <FaStar className="icon-star" />
        </a>
        <a href="/money" className="icon-money">
          <FaMoneyBill className="icon-money" />
        </a>
        <a href="/calendar" className="icon-calendar">
          <FaCalendarAlt className="icon-calendar" />
        </a>
      </div>
    </div>
  );
};

export default Menu;
