import React, { useState } from 'react';
import './App.css';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');

  const handleAdicionar = () => {
    // Aqui você pode adicionar a lógica para processar os valores de descrição e valor
    console.log(`Descrição: ${descricao}, Valor: ${valor}`);
    // Também é possível adicionar os valores a uma lista de despesas/receitas
    // ou realizar outras operações necessárias.
  };

  return (
    <div className="dashboard">
      <div className="sidebar-one">
        <h4>Seja bem-vindo</h4>
        {/* Aqui você pode adicionar links ou outros elementos da barra lateral */}
      </div>
      <div className="content">
        <h1>Dashboard</h1>
        <div className="tables">
          <div className="table">
            <h2>Receita Mensal</h2>
            {/* Adicione uma tabela com dados de receita mensal aqui */}
          </div>
          <div className="table">
            <h2>Despesa Mensal</h2>
            {/* Adicione uma tabela com dados de despesa mensal aqui */}
          </div>
          <div className="table">
            <h2>Saldo</h2>
            {/* Adicione uma tabela com dados de saldo aqui */}
          </div>
        </div>
        <div className="chart">
          <h2>Receita vs Despesa/Ano</h2>
          {/* Adicione um gráfico aqui */}
        </div>
        <div className="add-expense">
          <h2>Adicionar Despesa/Receita</h2>
          <div className="description-input">
            <label htmlFor="descricao">Descrição:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
