import React, { useState, useEffect } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);
  const [chartData, setChartData] = useState(null);
  const [categoriaDespesas, setCategoriaDespesas] = useState({});

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setContadorDespesas(contadorDespesas + 1);

    const categoriasAtualizadas = calcularCategoriaDespesas();
    setCategoriaDespesas(categoriasAtualizadas);
  };

  const calcularSaldoDisponivel = () => {
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoDisponivel = saldoTotal - totalDespesas;
    return { saldoDisponivel, totalDespesas };
  };

  const calcularCategoriaDespesas = () => {
    const categorias = {};
    registros.forEach((registro) => {
      const { categoria, valor } = registro;
      if (categoria in categorias) {
        categorias[categoria] += parseFloat(valor);
      } else {
        categorias[categoria] = parseFloat(valor);
      }
    });
    return categorias;
  };

  useEffect(() => {
    Chart.register(CategoryScale, LinearScale, BarElement, BarController);

    const chartData = {
      labels: Object.keys(categoriaDespesas),
      datasets: [
        {
          label: 'Despesas por Categoria',
          data: Object.values(categoriaDespesas),
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1,
        },
      ],
    };

    setChartData(chartData);
  }, [categoriaDespesas]);

  const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();

  return (
    <div className="dashboard">
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="saldo-total-input">
            <label htmlFor="saldoTotal">Saldo Total:</label>
            <input
              type="number"
              id="saldoTotal"
              value={saldoTotal}
              onChange={(e) => setSaldoTotal(e.target.value)}
            />
          </div>
          {/* ... (outros campos de entrada) */}
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
        <div className="chart-container">
          <h4>Gráfico de Saldo Total / Disponível</h4>
          <div className="chart-and-info">
            {chartData && (
              <Bar
                data={chartData}
                options={{
                  scales: {
                    y: {
                      beginAtZero: true,
                    },
                  },
                }}
              />
            )}
            <div className="info">
              <div className="saldo-disponivel">
                <p>Saldo Disponível:</p>
                <span>{saldoDisponivel}</span>
              </div>
              <div className="total-despesas">
                <p>Total de Despesas:</p>
                <span>{totalDespesas}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="table2">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
