import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBell, faUser, faAngleDown } from '@fortawesome/free-solid-svg-icons';

const HeaderCustomizado = () => {
  const [mostrarPopup, setMostrarPopup] = useState(false);
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState(0);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [valorAGuardar, setValorAGuardar] = useState(0);
  const [modoNoturno, setModoNoturno] = useState(false);

  const abrirPopup = () => {
    setMostrarPopup(true);
  };

  const fecharPopup = () => {
    setMostrarPopup(false);
  };

  const handleAdicionar = () => {
    // Lógica para adicionar despesa
    // Aqui você pode usar os estados para enviar os dados para onde desejar
  };

  const alternarModoNoturno = () => {
    setModoNoturno(!modoNoturno);
    // Lógica para alternar entre modo noturno e claro
  };

  return (
    <header className={`header-customizado ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="logo">Dashboard</div>
      <div className="icons-container">
        <FontAwesomeIcon icon={faBell} className="icon" />
        <div className="user-icon-container">
          <FontAwesomeIcon icon={faUser} className="icon" onClick={abrirPopup} />
          <FontAwesomeIcon icon={faAngleDown} className="arrow-icon" onClick={abrirPopup} />
        </div>
      </div>

      {mostrarPopup && (
        <div className="popup">
          <div className="popup-content">
            <button onClick={fecharPopup}>Fechar</button>
            <div className="add-expense-container">
              <div className="add-expense">
                <div className="saldo-total-input">
                  <label htmlFor="saldoTotal">Saldo Total:</label>
                  <input
                    type="number"
                    id="saldoTotal"
                    value={saldoTotal}
                    onChange={(e) => setSaldoTotal(e.target.value)}
                  />
                </div>
                {/* ... (restante do formulário de despesas) */}
                <button onClick={handleAdicionar}>Adicionar Despesa</button>
              </div>
              <button className="modo-noturno-button" onClick={alternarModoNoturno}>
                {modoNoturno ? "Modo Claro" : "Modo Noturno"}
              </button>
            </div>
          </div>
        </div>
      )}
    </header>
  );
};

export default HeaderCustomizado;
