import React, { useState } from 'react';
import { Bar } from 'react-chartjs-2';
import './App.css';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState(''); 
  const [categoria, setCategoria] = useState(''); 
  const [saldoDisponivel, setSaldoDisponivel] = useState(1000); // Exemplo de saldo disponível

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
  };

  // Função para calcular o valor total gasto
  const calcularValorTotalGasto = () => {
    return registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
  };

  // Efeito para atualizar o saldo disponível sempre que os registros são alterados
  React.useEffect(() => {
    const valorTotalGasto = calcularValorTotalGasto();
    const saldoRestante = saldoDisponivel - valorTotalGasto;
    setSaldoDisponivel(saldoRestante);
  }, [registros, saldoDisponivel]);

  // Dados para o gráfico
  const dataGrafico = {
    labels: ['Saldo Disponível', 'Valor Total Gasto'],
    datasets: [
      {
        label: 'Saldo vs. Gasto',
        backgroundColor: ['#36A2EB', '#FFCE56'],
        data: [saldoDisponivel, calcularValorTotalGasto()],
      },
    ],
  };

  return (
    <div className="dashboard">
      <div className="sidebar-one">
        <h4></h4>
        {/* Aqui você pode adicionar links ou outros elementos da barra lateral */}
      </div>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <input
              type="text"
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
      </div>
      <div className="table2">
        <table>
          <thead>
            <tr>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="chart-container">
        <Bar data={dataGrafico} />
      </div>
    </div>
  );
}

export default Dashboard;
