import React, { useState, useEffect } from 'react';
import './App.css';
import ReactApexChart from 'react-apexcharts'; // Importe o componente do ApexCharts

function Dashboard() {
  // ... (outro código existente)

  return (
    <div className="dashboard">
      <div className="add-expense-container">
        <div className="add-expense">
          {/* ... (código existente) */}
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
        <div className="chart-container">
          <h4>Gráfico de Saldo Total / Disponível</h4>
          <div className="chart-and-info">
            {chartData && (
              <ReactApexChart
                options={{
                  labels: ['Saldo Total', 'Saldo Disponível'],
                }}
                series={chartData.datasets[0].data}
                type="bar"
              />
            )}
            <div className="info">
              <div className="saldo-disponivel">
                <p>Saldo Disponível:</p>
                <span>{saldoDisponivel}</span>
              </div>
              <div className="total-despesas">
                <p>Total de Despesas:</p>
                <span>{totalDespesas}</span>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="table2">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="chart-pizza">
        <h4>Gráfico de Pizza de Distribuição de Categorias</h4>
        {categoriaData && (
          <ReactApexChart
            options={{
              labels: categoriaData.labels,
            }}
            series={categoriaData.data}
            type="donut" // Use "donut" para um gráfico de pizza
          />
        )}
      </div>
    </div>
  );
}

export default Dashboard;
