import React, { useState } from 'react';
import './App.css';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [registros, setRegistros] = useState([]);

  const handleAdicionar = () => {
    // Crie um novo registro com os valores atuais de descrição, valor, data e categoria
    const novoRegistro = { descricao, valor, data, categoria };

    // Adicione o novo registro à lista de registros
    setRegistros([...registros, novoRegistro]);

    // Limpe os campos de entrada após adicionar o registro
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
  };

  return (
    <div className="dashboard">
      <div className="sidebar-one">
        <h4></h4>
        {/* Aqui você pode adicionar links ou outros elementos da barra lateral */}
      </div>
      <div className="content">
        <div className="tables">
          <div className="table1">
            <div className="table-wrapper">
              <h2>Receita Mensal</h2>
              {/* Adicione uma tabela com dados de receita mensal aqui */}
            </div>
          </div>
          <div className="table1">
            <div className="table-wrapper">
              <h2>Despesa Mensal</h2>
              {/* Adicione uma tabela com dados de despesa mensal aqui */}
            </div>
          </div>
          <div className="table1">
            <div className="table-wrapper">
              <h2>Saldo</h2>
              {/* Adicione uma tabela com dados de saldo aqui */}
            </div>
          </div>
        </div>

        <div className="add-expense">
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <input
              type="text"
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
        
        <div className="table-one">
          <table>
            <thead>
              <tr>
                <th>Gastos</th>
                <th>Valor</th>
                <th>Data</th>
                <th>Categoria</th>
              </tr>
            </thead>
            <tbody>
            <tr className="separator">
            <td colSpan="4">---------</td>
            </tr>
              {registros.map((registro, index) => (
                <tr key={index}>
                  <td>{registro.descricao}</td>
                  <td>{registro.valor}</td>
                  <td>{registro.data}</td>
                  <td>{registro.categoria}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
    
  );
}

export default Dashboard;
