import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';

function App() {
  const [receitas, setReceitas] = useState([]);
  const [despesas, setDespesas] = useState([]);
  const [saldo, setSaldo] = useState(0);
  const [saldoDisponivel, setSaldoDisponivel] = useState(0);
  const [novaReceita, setNovaReceita] = useState({ descricao: '', valor: 0 });
  const [novaDespesa, setNovaDespesa] = useState({ descricao: '', valor: 0 });

  // Atualize o saldo disponível com base em receitas e despesas
  useEffect(() => {
    const totalReceitas = receitas.reduce((total, receita) => total + receita.valor, 0);
    const totalDespesas = despesas.reduce((total, despesa) => total + despesa.valor, 0);
    setSaldo(totalReceitas - totalDespesas);
  }, [receitas, despesas]);

  // Atualize o saldo disponível
  useEffect(() => {
    setSaldoDisponivel(saldo);
  }, [saldo]);

  // Função para adicionar receitas
  const adicionarReceita = () => {
    setReceitas([...receitas, novaReceita]);
    setNovaReceita({ descricao: '', valor: 0 });
  };

  // Função para adicionar despesas
  const adicionarDespesa = () => {
    setDespesas([...despesas, novaDespesa]);
    setNovaDespesa({ descricao: '', valor: 0 });
  };

  // Configuração de dados para o gráfico
  const chartData = {
    labels: ['Receitas', 'Despesas', 'Saldo Disponível'],
    datasets: [
      {
        label: 'Valores',
        data: [receitas.reduce((total, receita) => total + receita.valor, 0), despesas.reduce((total, despesa) => total + despesa.valor, 0), saldoDisponivel],
        backgroundColor: ['green', 'red', 'blue'],
      },
    ],
  };

  return (
    <div className="App">
      <h1>Controle Financeiro</h1>
      <div>
        <h2>Receitas</h2>
        <input
          type="text"
          placeholder="Descrição"
          value={novaReceita.descricao}
          onChange={(e) => setNovaReceita({ ...novaReceita, descricao: e.target.value })}
        />
        <input
          type="number"
          placeholder="Valor"
          value={novaReceita.valor}
          onChange={(e) => setNovaReceita({ ...novaReceita, valor: parseFloat(e.target.value) })}
        />
        <button onClick={adicionarReceita}>Adicionar Receita</button>
      </div>
      <div>
        <h2>Despesas</h2>
        <input
          type="text"
          placeholder="Descrição"
          value={novaDespesa.descricao}
          onChange={(e) => setNovaDespesa({ ...novaDespesa, descricao: e.target.value })}
        />
        <input
          type="number"
          placeholder="Valor"
          value={novaDespesa.valor}
          onChange={(e) => setNovaDespesa({ ...novaDespesa, valor: parseFloat(e.target.value) })}
        />
        <button onClick={adicionarDespesa}>Adicionar Despesa</button>
      </div>
      <div>
        <h2>Gráfico de Resumo</h2>
        {saldoDisponivel !== null && (
          <Bar
            data={chartData}
            options={{
              scales: {
                y: {
                  beginAtZero: true,
                },
              },
            }}
          />
        )}
      </div>
    </div>
  );
}

export default App;
