import React, { useState } from 'react';
import { FaChartBar, FaStar, FaMoneyBill, FaCalendarAlt } from 'react-icons/fa'; 

const Popup = ({ isOpen, onClose }) => {
  const [saldoTotal, setSaldoTotal] = useState('');
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');

  const handleAdicionar = () => {
    // Lógica para adicionar os dados (pode ser implementada conforme necessário)
    // Aqui você pode usar os valores de saldoTotal, descricao, valor, data e categoria
    console.log('Adicionar lógica aqui:', saldoTotal, descricao, valor, data, categoria);
    // Se desejar fechar o popup após adicionar os dados, chame a função onClose()
    // onClose();
  };

  return (
    <div className={`popup ${isOpen ? 'open' : ''}`}>
      <div className="popup-content">
        {/* Campos no popup */}
        <div className="saldo-total-input">
          <label htmlFor="saldoTotal">Saldo Total:</label>
          <input
            type="number"
            id="saldoTotal"
            value={saldoTotal}
            onChange={(e) => setSaldoTotal(e.target.value)}
          />
        </div>
        <div className="description-input">
          <label htmlFor="descricao">Gastos:</label>
          <input
            type="text"
            id="descricao"
            value={descricao}
            onChange={(e) => setDescricao(e.target.value)}
          />
        </div>
        <div className="value-input">
          <label htmlFor="valor">Valor:</label>
          <input
            type="number"
            id="valor"
            value={valor}
            onChange={(e) => setValor(e.target.value)}
          />
        </div>
        <div className="">
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <select
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              {/* Opções de categoria */}
            </select>
          </div>
        </div>
        <button onClick={handleAdicionar}>Adicionar</button>
      </div>
    </div>
  );
};

const Menu = () => {
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  const togglePopup = () => {
    setIsPopupOpen(!isPopupOpen);
  };

  return (
    <div className="menu-top">
      {/* Renderiza o popup se isPopupOpen for verdadeiro */}
      <Popup isOpen={isPopupOpen} onClose={togglePopup} />
      <div className="icon-buttons">
        {/* Adiciona um evento de clique para o ícone Dashboard */}
        <a href="/dashboard" className="icon-dashboard" onClick={togglePopup}>
          <FaChartBar className="icon-dashboard" />
        </a>
        {/* Outros ícones */}
      </div>
    </div>
  );
};

export default Menu;
