import React, { useState } from 'react';
import { FaChartBar, FaStar, FaMoneyBill, FaCalendarAlt } from 'react-icons/fa'; 

const Menu = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  return (
    <div className="menu-top">
      <div className={`menu-button ${menuOpen ? 'open' : ''}`} onClick={toggleMenu}>
        {/* Adicionei um texto "Menu" como exemplo */}
        <span>Menu</span>
      </div>
      {menuOpen && (
        <ul className="menu-items">
          {/* Adicionei alguns itens de menu como exemplo */}
          <li>Item 1</li>
          <li>Item 2</li>
          <li>Item 3</li>
        </ul>
      )}
      <div className="icon-buttons">
        <FaChartBar onClick={() => console.log('Dashboard clicado')} className="icon-dashboard" />
        <FaStar onClick={() => console.log('Estrela clicada')} className="icon-star" />
        <FaMoneyBill onClick={() => console.log('Cifrão clicado')} className="icon-money" />
        <FaCalendarAlt onClick={() => console.log('Calendário clicado')} className="icon-calendar" />
      </div>
    </div>
  );
};

export default Menu;
