import React, { useState } from 'react';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [exibirChartPizza, setExibirChartPizza] = useState(false);

  const handleAdicionar = () => {
    // Simulação de adição de registro
    setExibirChartPizza(true); // Exibir o chart-pizza quando um novo registro é adicionado
  };

  return (
    <div className="dashboard">
      <div className="add-expense-container">
      <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
      <div className="category-input">
            
            <label htmlFor="categoria">Categoria:</label>
            <select
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione uma categoria</option>
              <option value="Conta de Luz">Conta de Luz</option>
              <option value="Conta de Telefone">Conta de Telefone</option>
              <option value="Conta de Água">Conta de Água</option>
              <option value="Contas de Casa">Contas de Casa</option>
              <option value="Gastos Emergenciais">Gastos Emergenciais</option>
              <option value="Compras">Compras</option>
            </select>
          </div>
          
        <button onClick={handleAdicionar}>Adicionar Registro</button>
        <div className="body-container">
          {/* Condicional para renderizar o chart-pizza somente se exibirChartPizza for true */}
          {exibirChartPizza && (
            <div className="chart-pizza">
              <h4>Maiores gastos</h4>
              {/* Simulação de dados do gráfico */}
              <ReactApexChart
                options={{ chart: { type: 'donut' }, labels: ['A', 'B', 'C'] }}
                series={[20, 30, 50]}
                type="donut"
                width={380}
              />
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
