import React, { useState } from 'react';
import { FaChartBar, FaStar, FaMoneyBill, FaCalendarAlt } from 'react-icons/fa'; 

const Menu = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const handleDashboardClick = () => {
    console.log('Dashboard clicado');
    // Coloque aqui a lógica a ser executada ao clicar no ícone do Dashboard
  };

  const handleStarClick = () => {
    console.log('Estrela clicada');
    // Coloque aqui a lógica a ser executada ao clicar no ícone da Estrela
  };

  const handleMoneyClick = () => {
    console.log('Cifrão clicado');
    // Coloque aqui a lógica a ser executada ao clicar no ícone do Cifrão
  };

  const handleCalendarClick = () => {
    console.log('Calendário clicado');
    // Coloque aqui a lógica a ser executada ao clicar no ícone do Calendário
  };

  return (
    <div className="menu-top">
      <div className={`menu-button ${menuOpen ? 'open' : ''}`} onClick={toggleMenu}>
        {/* Adicionei um texto "Menu" como exemplo */}
        
      </div>
      {menuOpen && (
        <ul className="menu-items">
          
        </ul>
      )}
      <div className="icon-buttons">
        <button onClick={handleDashboardClick} className="icon-dashboard">
          <FaChartBar className="icon-dashboard" />
        </button>
        <button onClick={handleStarClick} className="icon-star">
          <FaStar className="icon-star" />
        </button>
        <button onClick={handleMoneyClick} className="icon-money">
          <FaMoneyBill className="icon-money" />
        </button>
        <button onClick={handleCalendarClick} className="icon-calendar">
          <FaCalendarAlt className="icon-calendar" />
        </button>
      </div>
    </div>
  );
};

export default Menu;
