import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar, Pie } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);
  const [categoriasDespesas, setCategoriasDespesas] = useState([]); // Lista de categorias de despesas
  const [despesasPorCategoria, setDespesasPorCategoria] = useState({}); // Total de despesas por categoria

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setContadorDespesas(contadorDespesas + 1);

    // Atualiza a lista de categorias de despesas
    if (!categoriasDespesas.includes(categoria)) {
      setCategoriasDespesas([...categoriasDespesas, categoria]);
    }

    // Atualiza o total de despesas por categoria
    setDespesasPorCategoria((prevDespesasPorCategoria) => {
      return {
        ...prevDespesasPorCategoria,
        [categoria]: (prevDespesasPorCategoria[categoria] || 0) + parseFloat(valor),
      };
    });
  };

  const calcularSaldoDisponivel = () => {
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoDisponivel = saldoTotal - totalDespesas;
    return { saldoDisponivel, totalDespesas };
  };

  useEffect(() => {
    Chart.register(CategoryScale, LinearScale, BarElement, BarController);

    const chartData = {
      labels: ['Saldo Total', 'Saldo Disponível'],
      datasets: [
        {
          label: 'Saldo',
          data: [saldoTotal, calcularSaldoDisponivel().saldoDisponivel],
          backgroundColor: ['blue', 'red'],
        },
      ],
    };

    setChartData(chartData);
  }, [saldoTotal, registros]);

  useEffect(() => {
    // Cria um gráfico de pizza com base nas categorias de despesas
    const pieData = {
      labels: categoriasDespesas,
      datasets: [
        {
          data: categoriasDespesas.map((categoria) => despesasPorCategoria[categoria] || 0),
          backgroundColor: [
            'red',
            'blue',
            'green',
            'yellow',
            'orange',
            'purple',
            'pink',
          ], // Cores das fatias
        },
      ],
    };

    setPieChartData(pieData);
  }, [registros, categoriasDespesas, despesasPorCategoria]);

  const [chartData, setChartData] = useState(null);
  const [pieChartData, setPieChartData] = useState(null);

  const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();

  return (
    <div className="dashboard">
      <div className="sidebar-one">
        <br></br>
        <br></br>
        <br></br>
        <label htmlFor="saldoTotal">Saldo Total:</label>
        <input
          type="number"
          id="saldoTotal"
          value={saldoTotal}
          onChange={(e) => setSaldoTotal(e.target.value)}
        />
      </div>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <input
              type="text"
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
        <div className="chart-container">
          <h4>Gráfico de Saldo Total / Disponível</h4>
          {chartData && (
            <Bar
              data={chartData}
              options={{
                scales: {
                  y: {
                    beginAtZero: true,
                  },
                },
              }}
            />
          )}
          <div className="saldo-disponivel">
            <p>Saldo Disponível:</p>
            <span>{saldoDisponivel}</span>
          </div>
          <div className="total-despesas">
            <p>Total de Despesas:</p>
            <span>{totalDespesas}</span>
          </div>
          <h4>Gráfico de Despesas por Categoria</h4>
          {pieChartData && (
            <Pie
              data={pieChartData}
              options={{
                responsive: true,
              }}
            />
          )}
        </div>
      </div>
      <div className="table2">
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
