import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [modoNoturno, setModoNoturno] = useState(false);
  const [dinheiroAGuardar, setDinheiroAGuardar] = useState(''); // Estado para o dinheiro a guardar
  const [porcentagemAGuardar, setPorcentagemAGuardar] = useState(null); // Estado para a porcentagem a guardar

  // Função para alternar entre o modo noturno e o modo claro
  const alternarModoNoturno = () => {
    setModoNoturno(!modoNoturno);
  };

  // Adicione efeitos de modo noturno ao corpo do documento com base no estado
  useEffect(() => {
    if (modoNoturno) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [modoNoturno]);

  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setContadorDespesas(contadorDespesas + 1);
  };

  // Restante do seu código...

  const handleAdicionarDinheiroAGuardar = () => {
    // Verificar se o valor inserido é um número válido
    const valor = parseFloat(dinheiroAGuardar);
    if (!isNaN(valor)) {
      // Calcular a porcentagem a guardar
      const porcentagem = (valor / saldoTotal) * 100;
      // Atualizar o estado com o novo valor de porcentagem
      setPorcentagemAGuardar(porcentagem);
    }
  };

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="saldo-total-input">
            <label htmlFor="saldoTotal">Saldo Total:</label>
            <input
              type="number"
              id="saldoTotal"
              value={saldoTotal}
              onChange={(e) => setSaldoTotal(e.target.value)}
            />
          </div>
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <select
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione uma categoria</option>
              <option value="Conta de Luz">Conta de Luz</option>
              <option value="Conta de Telefone">Conta de Telefone</option>
              <option value="Conta de Água">Conta de Água</option>
              <option value="Contas de Casa">Contas de Casa</option>
              <option value="Gastos Emergenciais">Gastos Emergenciais</option>
              <option value="Compras">Compras</option>
            </select>
          </div>
          <button onClick={handleAdicionar}>Adicionar Gasto</button>
          <button className="modo-noturno-button" onClick={alternarModoNoturno}>
            {modoNoturno ? "Modo Claro" : "Modo Noturno"}
          </button>
        </div>
        <div className="charts-container">
          <div className="chart-container">
            <h4>Gráfico de Onda dos Maiores Gastos por Mês</h4>
            {waveChartData && (
              <ReactApexChart options={waveChartData.options} series={waveChartData.series} type="line" />
            )}
          </div>
          
          {/* Formulário para dinheiro a guardar e exibição da porcentagem */}
          <div className="money-to-save-container">
            <h4>Dinheiro a Guardar</h4>
            <input
              type="number"
              placeholder="Digite o valor"
              value={dinheiroAGuardar}
              onChange={(e) => setDinheiroAGuardar(e.target.value)}
            />
            <button onClick={handleAdicionarDinheiroAGuardar}>Calcular</button>
            {typeof porcentagemAGuardar === 'number' && !isNaN(porcentagemAGuardar) && (
              <div className="money-saving-chart">
                <h5>Porcentagem a Guardar</h5>
                <ReactApexChart
                  options={{ labels: ['A Guardar', 'Restante'] }}
                  series={[porcentagemAGuardar, 100 - porcentagemAGuardar]}
                  type="donut"
                />
              </div>
            )}
          </div>
        </div>
      </div>
      
      {/* Restante do seu código... */}
    </div>
  );
}

export default Dashboard;
