import React, { useState, useEffect } from 'react';
import './App.css';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, BarController } from 'chart.js';
import ReactApexChart from 'react-apexcharts';

function Dashboard() {
  const [modoNoturno, setModoNoturno] = useState(false); // Estado do modo noturno
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);
  const [valorAGuardar, setValorAGuardar] = useState(0);

  const alternarModoNoturno = () => {
    setModoNoturno(!modoNoturno);
  };

  useEffect(() => {
    if (modoNoturno) {
      document.body.classList.add('dark-mode');
    } else {
      document.body.classList.remove('dark-mode');
    }
  }, [modoNoturno]);

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria, valorAGuardar };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setContadorDespesas(contadorDespesas + 1);
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoRestante = saldoTotal - totalDespesas;
    setSaldoTotal(saldoRestante);
  };

  const calcularPorcentagemAGuardar = () => {
    return (valorAGuardar / saldoTotal) * 100;
  };

  useEffect(() => {
    Chart.register(CategoryScale, LinearScale, BarElement, BarController);

    const chartData = {
      labels: ['Saldo Total', 'Saldo DisponÃ­vel'],
      datasets: [
        {
          label: 'Saldo',
          data: [saldoTotal, saldoTotal - totalDespesas],
          backgroundColor: ['blue', 'red'],
        },
      ],
    };

    setChartData(chartData);
  }, [saldoTotal, registros]);

  const [chartData, setChartData] = useState(null);

  const { totalDespesas } = calcularSaldoDisponivel();

  const calcularDistribuicaoCategorias = () => {
    const categorias = {};
    registros.forEach((registro) => {
      const { categoria } = registro;
      categorias[categoria] = categorias[categoria] ? categorias[categoria] + 1 : 1;
    });

    const labels = Object.keys(categorias);
    const data = Object.values(categorias);

    setCategoriaData({
      options: {
        labels,
      },
      series: data,
      type: 'donut',
    });
  };

  useEffect(() => {
    calcularDistribuicaoCategorias();
  }, [registros]);

  const [categoriaData, setCategoriaData] = useState(null);

  const [waveChartData, setWaveChartData] = useState({
    options: {
      chart: {
        id: 'wave-chart',
        type: 'line',
        height: 350,
      },
      xaxis: {
        categories: ['Jan', 'Fev', 'Mar', 'Abr', 'Mai', 'Jun', 'Jul', 'Ago', 'Set', 'Out', 'Nov', 'Dez'],
      },
    },
    series: [
      {
        name: 'Gastos',
        data: [1000, 1500, 1200, 1800, 2000, 1600, 1700, 1900, 2100, 2200, 2400, 2600],
      },
    ],
  });

  const [lucroChartData, setLucroChartData] = useState({
    options: {
      chart: {
        id: 'lucro-chart',
        type: 'radialBar',
      },
      plotOptions: {
        radialBar: {
          hollow: {
            size: '70%',
          },
          dataLabels: {
            showOn: 'always',
          },
        },
      },
      labels: ['Lucro'],
    },
    series: [calcularPorcentagemLucro()],
  });

  return (
    <div className={`dashboard ${modoNoturno ? 'dark-mode' : ''}`}>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="saldo-total-input">
            <label htmlFor="saldoTotal">Saldo Total:</label>
            <input
              type="number"
              id="saldoTotal"
              value={saldoTotal}
              onChange={(e) => setSaldoTotal(e.target.value)}
            />
          </div>
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <select
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            >
              <option value="">Selecione uma categoria</option>
              <option value="Conta de Luz">Conta de Luz</option>
              <option value="Conta de Telefone">Conta de Telefone</option>
              <option value="Conta de Ãgua">Conta de Ãgua</option>
              <option value="Contas de Casa">Contas de Casa</option>
              <option value="Gastos Emergenciais">Gastos Emergenciais</option>
              <option value="Compras">Compras</option>
            </select>
          </div>
          <div className="value-to-save-input">
            <label htmlFor="valorAGuardar">Quanto a guardar de dinheiro?</label>
            <input
              type="number"
              id="valorAGuardar"
              value={valorAGuardar}
              onChange={(e) => setValorAGuardar(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>$</button>
          <button className="modo-noturno-button" onClick={alternarModoNoturno}>
            {modoNoturno ? "Modo Claro" : "Modo Noturno"}
          </button>
        </div>
        <div className="charts-container">
          <div className="chart-container">
            <h4></h4>
            <div className="chart-and-info">
              {chartData && (
                <Bar
                  data={chartData}
                  options={{
                    scales: {
                      y: {
                        beginAtZero: true,
                      },
                    },
                  }}
                />
              )}
              <div className="info">
                <div className="saldo-disponivel">
                  <p>Saldo DisponÃ­vel:</p>
                  <span>{saldoTotal - totalDespesas}</span>
                </div>
                <div className="total-despesas">
                  <p>Total de Despesas:</p>
                  <span>{totalDespesas}</span>
                </div>
              </div>
            </div>
          </div>
          <div className="chart-pizza">
            <h4></h4>
            {categoriaData && (
