import React from 'react';
import { Doughnut } from 'react-chartjs-2';

const GraficoDePizza = ({ registros }) => {
  // Calcula a contagem de cada categoria
  const categorias = registros.reduce((acc, registro) => {
    if (registro.categoria in acc) {
      acc[registro.categoria]++;
    } else {
      acc[registro.categoria] = 1;
    }
    return acc;
  }, {});

  const labels = Object.keys(categorias);
  const data = Object.values(categorias);

  const dadosDoGrafico = {
    labels: labels,
    datasets: [
      {
        data: data,
        backgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
        hoverBackgroundColor: ['#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', '#9966FF'],
      },
    ],
  };

  const estiloGrafico = {
    width: '50%', // Defina o tamanho desejado, por exemplo, 50%
    margin: '0 auto', // Centralize o gráfico horizontalmente
  };

  return (
    <div className="grafico-de-pizza" style={estiloGrafico}>
      <h4>Gráfico de Pizza por Categoria</h4>
      <Doughnut data={dadosDoGrafico} />
    </div>
  );
};

export default GraficoDePizza;
