import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, LineElement, LineController } from 'chart.js';

function Dashboard() {
  const [descricao, setDescricao] = useState('');
  const [valor, setValor] = useState('');
  const [registros, setRegistros] = useState([]);
  const [data, setData] = useState('');
  const [categoria, setCategoria] = useState('');
  const [saldoTotal, setSaldoTotal] = useState(0);
  const [contadorDespesas, setContadorDespesas] = useState(0);

  const handleAdicionar = () => {
    const novoRegistro = { descricao, valor, data, categoria };
    setRegistros([...registros, novoRegistro]);
    setDescricao('');
    setValor('');
    setData('');
    setCategoria('');
    setContadorDespesas(contadorDespesas + 1);
  };

  const calcularSaldoDisponivel = () => {
    const totalDespesas = registros.reduce((total, registro) => total + parseFloat(registro.valor), 0);
    const saldoDisponivel = saldoTotal - totalDespesas;
    return { saldoDisponivel, totalDespesas };
  };

  useEffect(() => {
    Chart.register(CategoryScale, LinearScale, LineElement, LineController);
  }, []);

  useEffect(() => {
    const { saldoDisponivel, totalDespesas } = calcularSaldoDisponivel();
    const dataLabels = registros.map((registro) => registro.descricao);
    const dataValues = registros.map((registro) => parseFloat(registro.valor));

    const chartData = {
      labels: dataLabels,
      datasets: [
        {
          label: 'Valores',
          data: dataValues,
          borderColor: 'blue',
          borderWidth: 1,
          pointBackgroundColor: 'blue',
          tension: 0.3,
        },
      ],
    };

    setWaveChartData(chartData);
  }, [registros]);

  const [waveChartData, setWaveChartData] = useState(null);

  return (
    <div className="dashboard">
      <div className="sidebar-one">
        <br />
        <br />
        <br />
        <label htmlFor="saldoTotal">Saldo Total:</label>
        <input
          type="number"
          id="saldoTotal"
          value={saldoTotal}
          onChange={(e) => setSaldoTotal(e.target.value)}
        />
      </div>
      <div className="add-expense-container">
        <div className="add-expense">
          <div className="description-input">
            <label htmlFor="descricao">Gastos:</label>
            <input
              type="text"
              id="descricao"
              value={descricao}
              onChange={(e) => setDescricao(e.target.value)}
            />
          </div>
          <div className="value-input">
            <label htmlFor="valor">Valor:</label>
            <input
              type="number"
              id="valor"
              value={valor}
              onChange={(e) => setValor(e.target.value)}
            />
          </div>
          <div className="date-input">
            <label htmlFor="data">Data:</label>
            <input
              type="date"
              id="data"
              value={data}
              onChange={(e) => setData(e.target.value)}
            />
          </div>
          <div className="category-input">
            <label htmlFor="categoria">Categoria:</label>
            <input
              type="text"
              id="categoria"
              value={categoria}
              onChange={(e) => setCategoria(e.target.value)}
            />
          </div>
          <button onClick={handleAdicionar}>Adicionar</button>
        </div>
        <div className="chart-container">
          <h4>Gr√°fico de Ondas de Despesas</h4>
          {waveChartData && (
            <Line
              data={waveChartData}
              options={{
                maintainAspectRatio: false,
                scales: {
                  y: {
                    beginAtZero: true,
                  },
                },
              }}
            />
          )}
        </div>
      </div>
      <div className="table2" ref={tableRef}>
        <table>
          <thead>
            <tr>
              <th>#</th>
              <th>Gastos</th>
              <th>Valor</th>
              <th>Data</th>
              <th>Categoria</th>
            </tr>
          </thead>
          <tbody>
            {registros.map((registro, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{registro.descricao}</td>
                <td>{registro.valor}</td>
                <td>{registro.data}</td>
                <td>{registro.categoria}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Dashboard;
